import React, { useEffect, useRef } from 'react'
import { Editor } from '@tinymce/tinymce-react';
import ButtonPrimary from '../../components/Button';



export default function NoteEditor(props) {

    useEffect(()=>{
        return () => {
            alert("gf");
        }
    }, [])

const editorRef = useRef(null);


  const log = () => {
    if (editorRef.current) {
      console.log(editorRef.current.getContent());
    }
  };

  const onValueChange = (inst) => {
    alert(props.id);
    if (editorRef.current) {
      localStorage.setItem('currentEdition', props.id + "^%^sep" + props.title + "^%^sep" + props.book + "^%^sep" + editorRef.current.getContent().toString());
    }
  };

  return ( 
    <div align="left">
      <Editor  
        onInit={(evt, editor) => editorRef.current = editor}
        initialValue={localStorage.getItem('currentEdition')? localStorage.getItem('currentEdition').split("^%^sep")[3] : 'You can start taking notes!'}
        init={{
        onchange_callback : {onValueChange},
          height: 500,
          menubar: false,
          plugins: [
            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
          ],
          toolbar: 'undo redo | ' +
            'bold italic forecolor | alignleft aligncenter ' +
            'alignright alignjustify | bullist numlist outdent indent | ' +
            'removeformat | help',
          content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px;}}'
        }}
      /> 
      <br/>
      <ButtonPrimary onClick={log} text="Save"></ButtonPrimary>
    </div> 
  )
}
