import { Col, Row, Collapse, Button } from 'antd';
import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react';
import NoteEditor from './editor';
import Viewer from './viewer';
import { Editor } from '@tinymce/tinymce-react';
import ButtonPrimary from '../../components/Button';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';



export default function NoteMaker() {

   const [ currentNoteId, setCurrentNoteId] = useState("ll");
   const [ currentNoteTitle, setCurrentNoteTitle] = useState("ll");
   const [ currentBook, setCurrentBook] = useState("kk");
   const [ currentNoteBody, setCurrentNoteBody] = useState("<p>kk</p>");

  const [ edit, setEdit ] = useState(false);
  const [notes, setNotes ] = useState({
    "Book Title a" : [{noteId: "a", noteTitle: 'Sample note a'}],
    "Book Title b" : [{noteId: "b", noteTitle: 'Sample note b'}], 
    "Book Title c" : [{noteId: "b", noteTitle: 'Sample note b'}, {noteId: "b", noteTitle: 'Sample note b'}, {noteId: "b", noteTitle: 'Sample note b'}],  
    });


const editorRef = useRef(null);

  const log = () => {
    if (editorRef.current) {
      console.log(editorRef.current.getContent());
    }
  };


  return ( 
    <>
      <Row id="lp" align="center" style={{minHeight: '100vh'}}>
        <Col xs={{span: 0}} md={{span: 19}}>
            {
                edit && <div align="right">

                <Editor  
                  onInit={(evt, editor) => editorRef.current = editor}
                  initialValue={currentNoteBody}
                  init={{ 
                    height: 500,
                    menubar: false,
                    plugins: [
                      'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                      'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                      'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
                    ],
                    toolbar: 'undo redo | ' +
                      'bold italic forecolor | alignleft aligncenter ' +
                      'alignright alignjustify | bullist numlist outdent indent | ' +
                      'removeformat | help',
                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px;}}'
                  }}
                />  
              </div> 
            }
            {
                !edit && <Viewer rawHtmlBody={currentNoteBody} title={currentNoteTitle}/>
            }
            <button onClick={()=>{
                setEdit(edit? false :true);
                setCurrentNoteBody("<p>polo</p>")
            }}>fsfs</button>
        </Col>
        <Col xs={{span: 0}} md={{span: 5}} align="center">
        {!edit && <Collapse style={{fontWeight: '700', width: '100%'}} defaultActiveKey={['1']} onChange={()=>{}} align="left">
            {
                Object.keys(notes).length !== 0 ? Object.keys(notes).map((Key, i) => {
                    return <Collapse.Panel header={Key} key={i}>
                        <div align="left">
                        {
                            notes[Key].map(note => {
                                return <><Button align="left" title={note.noteTitle} style={{maxWidth: '70%', overflow: 'clip'}} onClick={()=>{
                                    // fetch note and set the variables in state with the returned data
                                    console.log(Key.toString(), note.noteId, note.noteTitle);
                                    setCurrentBook(Key.toString()); setCurrentNoteTitle(note.noteTitle); setCurrentNoteId(note.noteId); setCurrentNoteBody("lolpoo");
                                }} type="link">{note.noteTitle}</Button> 
                                <EditOutlined  style={{position: 'absolute', right: '30px'}} onClick={()=>{
                                    setCurrentBook(Key.toString()); setCurrentNoteTitle(note.noteTitle); setCurrentNoteId(note.noteId); setCurrentNoteBody("lolpoo");
                                    setEdit(true);
                                }}/>
                                &nbsp;
                                &nbsp;
                                <DeleteOutlined style={{position: 'absolute', right: '5px'}}/>
                                </>
                            })
                        }
                        </div>
                    </Collapse.Panel> 
                }) : ''
            }
    </Collapse>}

    {
        edit && <ButtonPrimary styl={{width: '70%'}} onClick={log} text="Save"></ButtonPrimary>
    }
        </Col>
      </Row>
    </> 
  )
}
