import { Col, Row, Collapse, Button, Input } from 'antd';
import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react';
import NoteEditor from './editor';
import Viewer from './viewer';
import { Editor } from '@tinymce/tinymce-react';
import ButtonPrimary from '../../components/Button';
import { DeleteOutlined, EditOutlined, LoadingOutlined } from '@ant-design/icons';
import { API_BASE } from '../../apiConfig';
import { bearer_token_key } from './../../localStorageConfig';


export default function NoteMaker() {

   const [ currentNoteId, setCurrentNoteId] = useState("ll");
   const [ currentNoteTitle, setCurrentNoteTitle] = useState("ll");
   const [ currentBook, setCurrentBook] = useState("kk");
   const [ currentNoteBody, setCurrentNoteBody] = useState("<p>kk</p>");

  const [ edit, setEdit ] = useState(false);
  const [notes, setNotes ] = useState(null);


const editorRef = useRef(null);

  useEffect(()=>{
    axios.post(API_BASE + './api/user/getUserNotes', {
        email: localStorage.getItem('bitsjoy_email')
    }, { 
        headers : {
        'Authorization' : localStorage.getItem(bearer_token_key),
        'Content-Type': 'application/json'
    }}).then(res => {
        setNotes(res.data);
        console.log(res.data);
    })
  }, [])

  const saveNote = () => {
    const date = new Date();
    if (editorRef.current) {
      console.log(editorRef.current.getContent());

      axios.post(API_BASE + '/api/notes/createnote', {
        "dateCreated": date,
        "dateUpdated": date,
        "title": currentNoteTitle,
        "body": editorRef.current.getContent(),
        "author": { 
                "id": localStorage.getItem('bitsjoy_userId'),
                "fullName": localStorage.getItem('bitsjoy_name'),
                "ppImageLink": localStorage.getItem('bitsjoy_pp_image')
        },
        "accessibleTo": [localStorage.getItem('bitsjoy_userId')],
        "bookTitle": currentBook
      },
      {
        headers : {
            'Authorization' : localStorage.getItem(bearer_token_key),
            'Content-Type': 'application/json'
        }
      }).then(()=>{

      })
    }
  };


  return ( 
    <>
      <Row id="lp" align="center">
        <Col xs={{span: 0}} md={{span: 18}}>
            {
                edit && <div align="right">
                <Input type="text" value={currentNoteTitle} onChange={(e)=>{
                    setCurrentNoteTitle(e.target.value);
                }} style={{fontWeight: '700'}} />
                <br/>
                <br/>
                <Editor  
                  onInit={(evt, editor) => editorRef.current = editor}
                  initialValue={currentNoteBody}
                  init={{ 
                    height: 500,
                    menubar: false,
                    plugins: [
                      'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                      'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                      'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
                    ],
                    toolbar: 'undo redo | ' +
                      'bold italic forecolor | alignleft aligncenter ' +
                      'alignright alignjustify | bullist numlist outdent indent | ' +
                      'removeformat | help',
                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px;}}'
                  }}
                />  
              </div> 
            }
            {
                !edit && <><Viewer rawHtmlBody={currentNoteBody} title={currentNoteTitle}/></>
            }

        </Col>
        <Col xs={{span: 0}} md={{span: 6}} align="center">
        {!edit && <Collapse style={{fontWeight: '700', width: '100%', border :'0px'}} defaultActiveKey={['1']} onChange={()=>{}} align="left">
            {
                notes ? Object.keys(notes).length !== 0 ? Object.keys(notes).map((Key, i) => {
                    return <Collapse.Panel style={{border: '0px'}} header={Key} key={i}>
                        <div align="left">
                        {
                            notes[Key].map(note => {
                                return <>

                                <Button align="left" title={note.noteTitle} style={{maxWidth: '70%',
                                overflow: 'hidden', textOverflow: 'ellipsis'}} onClick={()=>{
                                    // fetch note and set the variables in state with the returned data

                                    console.log(Key.toString(), note.noteId, note.noteTitle);
                                    setCurrentBook(Key.toString()); setCurrentNoteTitle(note.noteTitle); setCurrentNoteId(note.noteId); setCurrentNoteBody("lol sfsf<br/><br/>poo");
                                }} type="link">{note.noteTitle}</Button> 
                                &nbsp;
                                {
                                    currentNoteId === note.noteId && <EditOutlined  title="edit"  onClick={()=>{
                                        // setCurrentBook(Key.toString()); setCurrentNoteTitle(note.noteTitle); setCurrentNoteId(note.noteId); setCurrentNoteBody("lolpoo");
                                        setEdit(true);
                                    }}/>
                                 }
                                 <br/>
                                {/* <DeleteOutlined style={{position: 'absolute', right: '5px'}}/> */}
                                </>
                            })
                        }
                        </div>
                    </Collapse.Panel> 
                }) : '' : <LoadingOutlined />
            }
    </Collapse>}

    {
        edit && <ButtonPrimary styl={{width: '70%'}} onClick={saveNote} text="Save"></ButtonPrimary>
    }
        </Col>
      </Row>
    </> 
  )
}
